
public class TestRun {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("실행확인");
		int value; //변수선언
		value = 0; //변수 초기화
		int result = ++value;
		System.out.printf("result = %d , value = %d%n" , result, value);
		
		value = 0;
		int result2 = ++value;
		System.out.printf("result = %d , value = %d" , result2, value);
		
	

	}

}
